mapper.select:
SELECT user_id, user_name, user_address, user_email
FROM user
WHERE user_id = #{user_id}

SELECT user_id, user_name, user_address, user_email
FROM user
WHERE user_id = #{user_id}
    OR user_name = #{user_name}
    OR user_address = #{user_address}
    OR user_email = #{user_email}

mapper.update:
UPDATE USER
SET user_pwd = #{user_pwd},
    user_name = #{user_name},
    user_address = #{user_address}
    user_email = #{user_email}
WHERE user_id = #{user_id}
