mapper.select:
SELECT user_id, sidoName, gugunName, dongName
FROM favorite
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM favorite
WHERE user_id = #{userId}
    AND sidoName = #{sidoName}
    AND gugunName = #{gugunName}
    AND dongName = #{dongName}

mapper.select:
		SELECT user_id, sidoName, gugunName, dongName
        FROM favorite
        WHERE user_id = #{userId}
            AND sidoName = #{sidoName}
            AND gugunName = #{gugunName}
            AND dongName = #{dongName}
