mapper.update:
UPDATE notice
SET subject = #{subject}, content = #{content}
WHERE article_no = #{articleNo}

mapper.select:
SELECT article_no, user_id, subject, content, hit, reg_time
FROM notice
WHERE article_no = #{articleNo}

mapper.update:
UPDATE notice
SET hit = hit + 1
WHERE article_no = #{articleNo}

mapper.select:
SELECT article_no, user_id, subject, content, hit, reg_time
FROM notice
WHERE user_id = #{userId}
OR subject = #{subject}
