mapper.select:
SELECT user_id, user_pwd, type
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET user_name = #{userName}, user_address = #{userAddress}, user_email = #{userEmail}
WHERE user_id = #{userId}

UPDATE user
SET user_pwd = #{userPwd}
WHERE user_id = #{userId}

mapper.select:
SELECT user_id, user_name, user_address, user_email, join_date
FROM user
WHERE user_id = #{userId}

SELECT user_id
FROM user
WHERE user_name = #{userName} AND user_email = #{userEmail}

SELECT user_id, user_name, user_address, user_email, type, join_date
FROM user
WHERE user_id = #{userId}
    OR user_name = #{userName}
    OR user_address = #{userAddress}
    OR user_email = #{userEmail}

mapper.update:
UPDATE user
SET token = #{token}
WHERE user_id = #{userId}

mapper.select:
SELECT token
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET token = #{token, jdbcType=VARCHAR}
WHERE user_id = #{userId}
