mapper.select:
SELECT user_id, user_pwd, user_name, user_address, user_email
FROM user
WHERE user_id = #{userId}

SELECT user_id, user_pwd, user_name, user_address, user_email
FROM user
WHERE user_id = #{userId} AND user_pwd = #{userPwd}

SELECT user_id, user_name, user_address, user_email
FROM user
WHERE user_id = #{userId}
    OR user_name = #{userName}
    OR user_address = #{userAddress}
    OR user_email = #{userEmail}

mapper.update:
UPDATE USER
SET user_pwd = #{userPwd},
    user_name = #{userName},
    user_address = #{userAddress},
    user_email = #{userEmail}
WHERE user_id = #{userId}
